C:\WINDOWS\System32\WindowsPowerShell\v1.0\powershell.exe -NoProfile -NonInteractive -Command Write-Host "=== 5b. CHECK EXTERNAL IPs AGAINST THREAT INTEL ===" -ForegroundColor Cyan
$conns = (Invoke-RestMethod -Uri "http://localhost:8000/api/connections" -TimeoutSec 5).connections
# Extract IP from "IP:port" format and filter out local/private
$ips = $conns | ForEach-Object { 
    if ($_.remote) { ($_.remote -split ':')[0] }
} | Where-Object { 
    $_ -and $_ -notmatch '^(127\.|192\.168\.|10\.|172\.(1[6-9]|2[0-9]|3[01])\.)' 
} | Select-Object -Unique

Write-Host "External IPs found: $($ips.Count)"
$ips | ForEach-Object { Write-Host "  $_" }

if ($ips.Count -gt 0) {
    $body = @{ values = @($ips) } | ConvertTo-Json -Compress
    $result = Invoke-RestMethod -Uri "http://localhost:8000/api/edr/threat-intel/check" -Method POST -Body $body -ContentType "application/json"
    Write-Host "`nThreat Intel Check: $($result.total_checked) checked, $($result.matches) malicious"
}

C:\WINDOWS\System32\WindowsPowerShell\v1.0\powershell.exe -NoProfile -NonInteractive -Command Write-Host "=== TIMELINE EVENTS ===" -ForegroundColor Cyan
$timeline = Invoke-RestMethod -Uri "http://localhost:8000/api/edr/timeline?minutes=5" -TimeoutSec 5
Write-Host "Total: $($timeline.total)"
$timeline.events | Select-Object -First 5 | ForEach-Object {
    $mitre = if ($_.mitre_techniques.Count -gt 0) { $_.mitre_techniques -join ',' } else { '-' }
    Write-Host "  [$($_.event_type)] $($_.subject) -> $($_.target) | MITRE: $mitre"
}

C:\WINDOWS\System32\WindowsPowerShell\v1.0\powershell.exe -NoProfile -NonInteractive -Command Write-Host "=== MITRE COVERAGE ===" -ForegroundColor Cyan
$mitre = Invoke-RestMethod -Uri "http://localhost:8000/api/edr/mitre" -TimeoutSec 5
Write-Host "Techniques Detected: $($mitre.coverage.total_techniques_detected)"
$mitre.coverage.tactics | Where-Object { $_.techniques_detected -gt 0 } | ForEach-Object {
    Write-Host "  $($_.name): $($_.techniques_detected) techniques"
    $_.techniques | ForEach-Object {
        Write-Host "    - $($_.id): $($_.name)"
    }
}
